import React, { useState } from 'react';



// const NotificationForm = () => {
//     return (
//         <form>
//             <h1>Nové oznámení</h1>
//             <div>
//                 <label>Kategorie *</label>
//                 <select>
//                     <option>Vyberte jednu možnost</option>
//                 </select>
//             </div>
//             {/* Добавьте остальные поля формы */}
//         </form>
//     );
// };

// export default NotificationForm;



interface NotificationFormData {
    category: string;
    whoFeelsBad: string;
    class: string;
    details: string;
}

const NotificationForm = () => {
    const [formData, setFormData] = useState<NotificationFormData>({
        category: '',
        whoFeelsBad: '',
        class: '',
        details: ''
    });

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log(formData);
    };

    return (
        <div className="min-h-screen bg-white flex flex-col items-center py-12 px-4">
            <div className="w-full max-w-2xl">
                <h1 className="text-3xl font-bold text-[#0A2540] text-center mb-8">Nové oznámení</h1>
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label className="block text-[#4F6C8D] mb-2">
                            Kategorie
                            <span className="text-red-500">*</span>
                        </label>
                        <select className="w-full p-3 border border-[#E5E9F2] rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500" value={formData.category} onChange={(e) => setFormData({ ...formData, category: e.target.value })} required>
                            <option value="">Problémy doma</option>
                        </select>
                    </div>
                    <div>
                        <label className="block text-[#4F6C8D] mb-2">Kdo se necítí dobře?<span className="text-red-500">*</span></label>
                        <input type="text" className="w-full p-3 border border-[#E5E9F2] rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500" value={formData.whoFeelsBad} onChange={(e) => setFormData({ ...formData, whoFeelsBad: e.target.value })} required/>
                    </div>
                    <div>
                        <label className="block text-[#4F6C8D] mb-2">Třída<span className="text-red-500">*</span></label>
                        <input type="text" className="w-full p-3 border border-[#E5E9F2] rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500" value={formData.class} onChange={(e) => setFormData({ ...formData, class: e.target.value })} required />
                    </div>
                    <div>
                        <label className="block text-[#4F6C8D] mb-2">
                            Více informací <span className="text-gray-400">(?)</span>
                        </label>
                        <textarea
                            className="w-full p-3 border border-[#E5E9F2] rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 min-h-[200px]"
                            placeholder="Uveďte prosím co nejvíce podrobností..."
                            value={formData.details}
                            onChange={(e) => setFormData({ ...formData, details: e.target.value })}
                        />
                    </div>

                    <div className="text-sm text-[#4F6C8D]">
                        Seznamte se s informacemi o tom, {' '}
                        <a href="#" className="text-blue-500 hover:underline">
                            jak zpracováváme osobní údaje
                        </a>
                        .
                    </div>

                    <button
                        type="submit"
                        className="w-full bg-[#0095FF] text-white py-3 px-4 rounded-xl hover:bg-blue-600 transition-colors"
                    >
                        Odeslat
                    </button>
                </form>
            </div>
        </div>
    );
};

export default NotificationForm;
