import React, { useState } from 'react';



// const NotificationForm = () => {
//     return (
//         <form>
//             <h1>Nové oznámení</h1>
//             <div>
//                 <label>Kategorie *</label>
//                 <select>
//                     <option>Vyberte jednu možnost</option>
//                 </select>
//             </div>
//             {/* Добавьте остальные поля формы */}
//         </form>
//     );
// };

// export default NotificationForm;

interface NotificationFormData {
  category: string;
  senderName: string;
  senderAge: string;
  file: File | null;
}

const NotificationForm: React.FC = () => {
  const [formData, setFormData] = useState<NotificationFormData>({
    category: '',
    senderName: '',
    senderAge: '',
    file: null
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Обработка отправки формы
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8 bg-white p-8 rounded-lg shadow">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-6">
            Nové oznámení
          </h1>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Kategorie <span className="text-red-500">*</span>
            </label>
            <select 
              className="mt-1 block w-full pl-3 pr-10 py-2 text-base border border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 rounded-md"
              value={formData.category}
              onChange={(e) => setFormData({...formData, category: e.target.value})}
            >
              <option value="">Vyberte jednu možnost</option>
              <option value="sikana">Šikana, špatné chování</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Jméno odesílatele
            </label>
            <input
              type="text"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              value={formData.senderName}
              onChange={(e) => setFormData({...formData, senderName: e.target.value})}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Věk odesílatele
            </label>
            <input
              type="number"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              value={formData.senderAge}
              onChange={(e) => setFormData({...formData, senderAge: e.target.value})}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Nahrát soubor
            </label>
            <input
              type="file"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              onChange={(e) => setFormData({...formData, file: e.target.files?.[0] || null})}
            />
          </div>

          <button
            type="submit"
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            Pokračovat
          </button>
        </form>
      </div>
    </div>
  );
};

export default NotificationForm;
